The byte offset should be signed. 

```c
#include <stddef.h>
#include <stdio.h>
#include <string.h>

struct s { int a; char b; };

int main(int argc, char *argv[]) {
  char carr[] = "abcdefg";
  char *cptr = strchr(carr, 'd');
  ptrdiff_t cres = cptr - carr;
  ptrdiff_t cresNeg = carr - cptr;
  printf("cres: %ld ; %ld\n", cres, cresNeg);

  int iarr[] = { 1, 2, 3, 4, 5, 6, 7, 0 };
  int *iptr = iarr + 5;
  ptrdiff_t ires = iptr - iarr;
  ptrdiff_t iresNeg = iarr - iptr;
  printf("ires: %ld ; %ld\n", ires, iresNeg);

  struct s sarr[] = { { 1, 'a' }, { 1, 'a' }, { 1, 'a' } };
  struct s *sptr = &sarr[2];
  ptrdiff_t sres = sptr - sarr;
  ptrdiff_t sresNeg = sarr - sptr;
  printf("sres: %ld ; %ld\n", sres, sresNeg);

  return 0;
}
